mapper.insert:
INSERT INTO Notification (
    user_id, content, type, is_read, created_at, reference_id
) values (
             #{userId}, #{content}, #{type}, #{isRead}, NOW(), #{referenceId}
         )

mapper.update:
update Notification
set is_read = true
where notification_id = #{notificationId}

UPDATE Notification
SET content = #{content}
WHERE notification_id = #{notificationId}

mapper.select:
SELECT *
FROM Notification
WHERE user_id = #{userId}
  AND type = #{type}
  AND reference_id = #{referenceId}
  AND is_read = false
    LIMIT 1
